def main(inpt):
    floor = 0
    for c in inpt:
        if c == "(":
            floor += 1
        elif c == ")":
            floor -= 1
    return floor


def main2(inpt):
    floor = 0
    for i, c in enumerate(inpt):
        if c == "(":
            floor += 1
        elif c == ")":
            floor -= 1
        if floor == -1:
            return i + 1
    return -1


