def main():
    def one(string):
        for i, _ in enumerate(string):
            s = i - 4
            if i > 3:
                sub = string[s:i]
                if len(set(sub)) == 4:
                    return i, sub

    stings = [
        "bvwbjplbgvbhsrlpgdmjqwftvncz",
        "nppdvjthqldpwncqszvftbrmjlhg",
        "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",
        "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw",
        "",
    ]
    for string in stings:
        print(one(string))

    def two(string):
        for i, _ in enumerate(string):
            s = i - 14
            if i > 13:
                sub = string[s:i]
                if len(set(sub)) == 14:
                    return i, sub

    stings = [
        "mjqjpqmgbljsphdztnvjfqwrcgsmlb",
        "bvwbjplbgvbhsrlpgdmjqwftvncz",
        "nppdvjthqldpwncqszvftbrmjlhg",
        "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",
        "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw",
        "",
    ]
    for string in stings:
        print(two(string))


if __name__ == "__main__":
    main()
